import React from 'react'
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";

export default function List({todoData, setTodoData}) {

    const handleClick = (id) => {
        let newToDoData = todoData.filter(data => data.id !== id)
        setTodoData(newToDoData);
    };

    const handleCompleteChange = (id) => {
        let newToDoData = todoData.map((data) => {
            if (data.id === id) {
            data.completed = !data.completed;
            }
            return data;
        });
        setTodoData(newToDoData);
    };

  return (
    <div>
        <DragDropContext>
            <Droppable droppableId='todo'>
                {(provided) => (
                    <div {...provided.droppableProps} ref={provided.innerRef}>
                        {todoData.map((data, index) => (
                            <Draggable
                                key = {data.id}
                                draggableId = {data.id.toString()}
                                index = {index}
                                >
                                {(provided, snapshot) => (

                                    <div key={data.id} {...provided.draggableProps} ref={provided.innerRef} {...provided.dragHandleProps}>
                                        <div className={`${snapshot.isDragging ? "bg-gray-400" : "bg-gray-100"}flex items-center justify-between w-full px-4
                                            py-1 my-2 text-gray-600 bg-gray-100 border rounded`}>
                                            <input 
                                            type="checkbox" 
                                            onChange= {() => handleCompleteChange(data.id)}
                                            defaultChecked={false} />
                                            <span className={data.completed && "line-through"}>{" "}{data.title}</span>
                                            <button className="px-4 py-2 float-right" onClick={() => handleClick(data.id)}>x</button>
                                        </div>
                                    </div>
                                 )}
                                </Draggable>
                            ))}  
                            {provided.placeholder}  
                        </div>
                    )}

                </Droppable>
            </DragDropContext>
        </div>
    )
}
